package com.avinash.training;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      //syntax;
	  /*  //entry control loop
	   * initialization
	   * while(condition){
	   *   //statements
	   *   increment/decrement
	   * }
	   * 
	   * Amstrong 153 -> 1*1*1 + 5*5*5 + 3*3*3 = 153
	   */
	
		int i =10;
		while(i<=9){
			System.out.print(i + ",");
			i = i + 2;
		}
		
	}

}
