package com.microsoft.internerbanking.controller;


public class Assignment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int A = 10;
		int C = 20;
		
		System.out.println(" USE OF += " + (C += A)); // C = C + A

		System.out.println(" USE OF -= " + (C -= A)); // C = C - A
		
		System.out.println(" USE OF *= " + (C *= A)); // C = C * A
		
		System.out.println(" USE OF /= " + (C /= A)); // C = C/A
		
		System.out.println(" USE OF %= " + (C %= A)); // C = C % A
		
	}

}
